                var placement = $(element).data('error');
                if (placement) {
                    $(placement).append(error)
                } else {
                    error.insertAfter(element);
                }

            },
            submitHandler: function () {
                $.post(orderForm.attr("action"), orderForm.serialize()+"&lang=en").done(function (data) {
                    console.log(data)
                    if (data.code === true) {
