                        $('.basket-results, .total-sum').fadeOut();
                        $('.basket-empty').removeClass('hide');
                        _amountProductsCart();
                        _totalPriceCart();
                    }
                })
            }
        });
    };

    // Возврат объектов, назначенных модулю
    return {
        init: function() {
            _cartAction();
            _amountProductsCart();
            _orderForm();
        }
    }

}();

