                    _amountProductsCart();
                    _totalPriceCart();
                }
            });
        });

        // удаление товаров из корзины
        $('.remove-all-products').on('click', function() {
            $.post('/public/app.php', { act: $(this).attr('data-action'), lang: 'en' }, function(data) {
