                        } else {
                            $('#errors span').remove();
                            $('#errors').append('<span>' + data.message + '</span>');
                        }
                    });
                }
            });

        };

        var _subscribeConfirmForm = function () {
            var  subscribeConfirmForm = $('#subscription-verify');

            // форма
            subscribeConfirmForm.validate({
                ignore: [],
                errorElement: 'span',
